 function getPages(){
     var category=$('#category').val();
     var date = new Date($('#date-input').val());
  	 var day = date.getDate();
 	 var month = date.getMonth() + 1;
  	 var year = date.getFullYear();
    if (month.toString().length == 1) {
    month = "0" + month
}
if (day.toString().length == 1) {
    day = "0" + day
}
var fulldate =year.toString() + "-" + month.toString() + "-" + day.toString();
	alert("full date is "+year.toString() + "-" + month.toString() + "-" + day.toString());
    alert("Category name is "+category);

    $.ajax({
        type : "GET",
        headers : {
					Accept : "application/json; charset=utf-8",
					"Content-Type" : "application/json; charset=utf-8"
				},
        url : "/bin/categorypathbasedexample",
        cache: false,
         data: {
		categoryselected:category,
        sdate:fulldate
		},
        success : function(response) {
        var values = [];
		values = response;
   		 for(var i = 0; i < values.length; i++){

     	$('#listofpages').append(values[i]+"</br>");
     	}
                }
  });
   }

